mapper.select:
WITH sub AS ( SELECT railway.railway_no , railway.railway_name , railway.railway_company_name FROM transportation_info.railway railway INNER JOIN transportation_info.area_prefecture area ON railway.prefecture_no = area.prefecture_no WHERE area.area_no = '${hissu.ar}' AND area.prefecture_no = '${hissu.ra}' AND railway.railway_no in
'${rn}'
) SELECT sub.railway_name , metro.railway_no , metro.metro_no , metro.metro_name FROM transportation_info.metro INNER JOIN sub ON metro.railway_no = sub.railway_no AND metro.railway_name = sub.railway_name
